import { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { newCan, getTemps } from '../../redux/actions/actions';
import { _Create } from '../../stl-cmp';

const CreateCan = ({ temps, getTemps }) => {

    useEffect(() => {
        (async ()=>{
            await getTemps()
        })()
    }, [getTemps])

	const [ state, setState ] = useState({
		name: '',
		picture: '',
		height: '',
		weight: '',
		yearsOfLife: '',
		temps: []
	});

    const [minMax, setMinMax] = useState({
        hMin: 0,
        hMax: 0,
        wMin: 0,
        wMax: 0,
    })

    const handlerChange=({target: {value, name}})=>{
        if(name === 'temps'){
            const _temps = [...state.temps, value]
            setState({
                ...state,
                [name]: _temps
            })
        }else if(name[0]==='w' || name[0]==='h'){
            setMinMax({
                ...state,
                [name]: value
            })
        }else{
            setState({
                ...state,
                [name]:value
            })
        }
    }

    const submitCan = ()=>{
        setState({
            ...state,
            height: `${minMax.hMin} - ${minMax.hMax}`,
            weight: `${minMax.wMin} - ${minMax.wMax}`
        })
    }



	return (
		<_Create>
			<form onSubmit={(e)=>{
                e.preventDefault();
                submitCan();
                newCan(state)
            }}>
				<div className="img" style={{background: state.picture.length?`url(${state.picture})`:'', backgroundPosition: 'center', WebkitBackgroundSize: 'cover'}}>
					<img src={state.picture} alt="" required />
				</div>
				<div className="desc">
					<label>
						<input id='name' type="text" onChange={handlerChange} placeholder="Nombre de la raza..." name="name" value={state.name} required minLength='3'/>
					</label>
                        <div className="body">
                            <div className="temps">

                            </div>
                            <div className="form"></div>
                        </div>
                    <button type='submit' >Crear</button>
				</div>
			</form>
		</_Create>
	);
};

const mapStateToProps = ({ temps }) => ({ temps });

export default connect(mapStateToProps, { getTemps })(CreateCan);
