import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { getTemps, filterBy, activePage } from "../../../redux/actions/actions";
import { _Filter } from "../../../stl-cmp";

const Filter = ({temps, filterBy, getTemps, activePage}) => {
    useEffect(()=>{
        (async function (){
            return (await getTemps());
        })()
    }, [getTemps])

    const [temperamentos, setTemps] = useState([])

    const filterCans = ({target: {value}}, valor = null)=>{
        filterBy(value);
        activePage(0)
        if(valor){
            setTemps([...temperamentos, value])
        }
    }
    const cleaner = ()=>{
        filterBy('CLEAN');
        document.getElementById('finder').value="";
        document.getElementById('disabled').selected=true;
        document.querySelectorAll('.radios').forEach((e)=>(e.checked=false));
        setTemps([])
    }
    return (
        <_Filter>

            <label>
                <p>Base de datos </p>
           <input type="radio" className="radios" onChange={filterCans} name="filter" value="MY_CANS" />
            </label>
           <label>
               <p>Api de perros</p>
           <input type="radio" className="radios" onChange={filterCans} name="filter" value="API_DOGS" />
            </label>
           <label>
               <p>Todos los perros</p>
           <input type="radio" className="radios" onChange={filterCans} name="filter" value="TODOS"/>
            </label>
            <div>
            <select name="" id="" onChange={(e)=>{filterCans(e, true)}}>
                <option id="disabled" value="" selected disabled>Filtrar por temperamento...</option>
                {temps.map((temp, i) => {
                    return (
                        <option key={i.toString()} value={temp.name}>{temp.name}</option>
                    )
                })}
            </select>
            <div>
                {temperamentos.map(e=>e)}
            </div>
            </div>
            <button onClick={cleaner}>❌</button>

        </_Filter>
    )
}

const mapStateToProps = ({temps})=>({temps})

export default connect(mapStateToProps, {filterBy, getTemps, activePage})(Filter)
