import styled from 'styled-components';
import canSleeped from '../img/can-sleeped.jpg';

export const Landing = styled.div`
width: 100vw;
height: 100vh;
background: linear-gradient(to top, #000, #333);
background: url(${canSleeped});
background-position: 55% 55%;
background-size: 59%;
background-repeat: repeat;
/* padding: 3rem;  */
display: flex;
justify-content: center;
align-items: end;
overflow: hidden;
&::after{
    content: '';
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    backdrop-filter: brightness(1.3) contrast(1.15);
    background:linear-gradient(to right, #101010 18%, #0000 30%, #0000 70%, #101010 82%) ;
}
&::before{
    content: '';
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    /* backdrop-filter: brightness(1.5); */
    background:radial-gradient(65rem, #0000 50%, #121213aa 65%) ;
}
& button{
    z-index: 1;
    background: transparent;
    border: none;
    position: relative;
    cursor: pointer;
    &:hover{
        filter: brightness(.7);
    }
    &:active{
        filter: brightness(.4);
    }
    & img{
        width: 10rem;
    }
    & p {
        position: absolute;
        top: 30%;
        left: 20%;
    }
}
& .Container{
    z-index: 1;
    height: 100%;
    width: 95%;
    border: 1px solid brown;
}
`
