import { useState } from 'react';
import { connect } from 'react-redux';
import { newCan } from '../../redux/actions/actions';
import { _Create } from '../../stl-cmp';

const CreateCan = ({ temps, newCan }) => {
	const [ state, setState ] = useState({
		name: '',
		picture: '',
		height: '',
		weight: '',
		yearsOfLife: '',
		temps: []
	});

    const [minMax, setMinMax] = useState({
        hMin: 0,
        hMax: 0,
        wMin: 0,
        wMax: 0,
    })

    const handlerChange=({target: {value, name}})=>{
        if(name === 'temps'){
            const _temps = [...state.temps, value]
            setState({
                ...state,
                [name]: _temps
            })
        }else if(name[0]==='w' || name[0]==='h'){
            setMinMax({
                ...state,
                [name]: value
            })
        }else{
            setState({
                ...state,
                [name]:value
            })
        }
    }

    const submitCan = ()=>{
        newCan(state)
    }



	return (
		<_Create>
			<form onSubmit={(e)=>{
                e.preventDefault();
                submitCan()
            }}>
				<div className="img" style={{background: state.picture.length?`url(${state.picture})`:'', backgroundPosition: 'center', WebkitBackgroundSize: 'cover'}}>
					<img src={state.picture} alt="" required />
				</div>
				<div className="desc">
					<label>
						<input id='name' type="text" onChange={handlerChange} placeholder="Nombre de la raza..." name="name" value={state.name} required minLength='3'/>
					</label>
					<label>

						<input type="text" onChange={handlerChange} name="picture" placeholder='Link de la imagen...' value={state.picture} required pattern='(http[s]?:\/\/.*\.(?:png|jpg|gif|svg|jpeg))'/>
					</label>
					<label >
						<input type="number" onChange={handlerChange} name="hMin" placeholder='Altura minima...' value={minMax.hMin} required maxLength='2' minLength='1'/>
					</label>
					<label >
						<input type="number" onChange={handlerChange} name="hMax" placeholder='Altura maxima...' value={minMax.hMax} required maxLength='2' minLength='1' />
					</label>
					<label>
						<input type="number" onChange={handlerChange} name="wMin" placeholder='Peso Minimo' value={minMax.wMin}  required maxLength='2' minLength='1' />
					</label>
					<label >
						<input type="number" onChange={handlerChange} name="wMax" placeholder='Peso maximo...' value={minMax.wMax} required maxLength='2' minLength='1' />
					</label>
					<label>
						<input type="number" onChange={handlerChange} name="yearsOfLife" placeholder='Años de vida ...' value={state.yearsOfLife}  required maxLength='2' minLength='1' />
					</label>
                    <button type='submit' >Crear</button>
				</div>
			</form>
		</_Create>
	);
};

const mapStateToProps = ({ temps }) => ({ temps });

export default connect(mapStateToProps, { newCan })(CreateCan);
