import * as _ from '../actions/types';
const initialState = {
	cans: [],
	can: {},
	temps: [],
	filter: [],
	pagination: [],
	pag: []
};

const rootReducer = function(state = initialState, { type, payload }) {

	switch (type) {
		case _.GET_CANS:
			return {
				...state,
				cans: payload,
				filter: payload
			};
		case _.CAN:
			const can = state.cans.find((can) => can.id === payload);
			return {
				...state,
				can: can
			};
		case _.TEMPS:
			return {
				...state,
				temps: payload
			};
		case _.PAGINATION:
