				let cuts = state.cans.slice(i, i + 8);
				pages.push(cuts);
			}
			return {
				...state,
				pagination: pages
			};
		case _.PAG:
			let pag = state.pagination[payload];
			return {
				...state,
				pag: pag
			};
		case _.SEARCH:
			const cans = state.cans.filter((can) => {
				if (payload === '') return can;
				if (payload && can.name.toUpperCase().includes(payload.toUpperCase())) return can;
