import styled from 'styled-components';
import canSleeped from '../img/can-sleeped.jpg';

export const Landing = styled.div`
width: 100vw;
height: 100vh;
background: linear-gradient(to top, #000, #333);
background: url(${canSleeped});
background-position: center 55%;
background-size: 59%;
/* background-repeat: no-repeat; */
padding: 3rem; 
display: flex;
justify-content: center;
align-items: end;
overflow: hidden;
&::after{
    content: '';
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    backdrop-filter: brightness(1.5) contrast(1.2);
    background:linear-gradient(to right, #090911 21%, #0000 30%, #0000 70%, #090911 79%) ;
}
&::before{
    content: '';
    display: block;
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    /* backdrop-filter: brightness(1.5); */
    background:radial-gradient(60rem, #0000 50%, #121213 65%) ;
}
& button{
    background: transparent;
    border: none;
    position: relative;
    cursor: pointer;
    &:hover{
        filter: brightness(.7);
    }
    &:active{
        filter: brightness(.4);
    }
    & img{
        width: 10rem;
    }
    & p {
        position: absolute;
        top: 30%;
        left: 20%;
    }
}
`
