import { connect } from "react-redux"
import { activePage } from "../../redux/actions/actions"
import { _Pagination } from "../../stl-cmp"
const Pagination = ({pagination, activePage, page}) => {
    useEffect(() => {
        document.querySelectorAll('button').forEach(e=>{
            e.classList.remove()
        })
    }, [page])
    const ChangePag=(e)=>{activePage(e.target.value); e.target.classList.add('active')}
    return (
        <_Pagination>
            {pagination.map((e, i)=>{
                return <button onClick={ChangePag} key={i.toString()} value={i}>{i+1}</button>
            })}
        </_Pagination>
    )
}

const mapStateToProps=({pagination, page})=>({pagination, page})

export default connect(mapStateToProps, {activePage})(Pagination)
